import "./profile.scss";
import FacebookTwoToneIcon from "@mui/icons-material/FacebookTwoTone";
import LinkedInIcon from "@mui/icons-material/LinkedIn";
import InstagramIcon from "@mui/icons-material/Instagram";
import PinterestIcon from "@mui/icons-material/Pinterest";
import TwitterIcon from "@mui/icons-material/Twitter";
import PlaceIcon from "@mui/icons-material/Place";
import LanguageIcon from "@mui/icons-material/Language";
import EmailOutlinedIcon from "@mui/icons-material/EmailOutlined";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import Posts from "../../components/posts/Posts"
import { useQuery, useQueryClient, useMutation } from '@tanstack/react-query'
import { makeRequest } from "../../axios";
import { useLocation } from "react-router-dom";
import { AuthContext } from "../../context/authContext";
import { useContext } from "react";


const Profile = () => {

  const userId = useLocation().pathname.split("/")[2];

  const {currentUser} = useContext(AuthContext);

  const { isLoading, error, data = [] } = useQuery({
    queryKey: ["user"],
    queryFn: () => makeRequest.get("/users/find/" + userId).then(res => res.data || []),
  });

  const {isLoading: rIsLoading, data: relationshipData = [] } = useQuery({
    queryKey: ["relationship"],
    queryFn: () => makeRequest.get("/relationships?followedUserId=" + userId).then(res => res.data || []),
  });

  const queryClient = useQueryClient();

  const mutation = useMutation({
    mutationFn: (following) => {
      if (following) {
        return makeRequest.delete(`/relationships?userId=${userI}`);
      }
      return makeRequest.post("/relationships", { postId: post.id });
    },
    onSuccess: () => {
      queryClient.invalidateQueries(["relationships"]);
    }
  });
  

  const handleFollow = () => {
    if (!data) return; 
    mutation.mutate(data.includes(currentUser.id));
  };
  

  return (
    <div className="profile">
         {isLoading ? (
        "loading"
      ) : (
        <>
      <div className="images">
        <img
          src={data.coverPic}
          alt=""
          className="cover"
        />
        <img
          src={data.profilePic}
          alt=""
          className="profilePic"
        />
      </div>
      <div className="profileContainer">
        <div className="uInfo">
          <div className="left">
            <a href="http://facebook.com">
              <FacebookTwoToneIcon fontSize="large" />
            </a>
            <a href="http://facebook.com">
              <InstagramIcon fontSize="large" />
            </a>
            <a href="http://facebook.com">
              <TwitterIcon fontSize="large" />
            </a>
            <a href="http://facebook.com">
              <LinkedInIcon fontSize="large" />
            </a>
            <a href="http://facebook.com">
              <PinterestIcon fontSize="large" />
            </a>
          </div>
          <div className="center">
            <span>{data.name}</span>
            <div className="info">
              <div className="item">
                <PlaceIcon />
                <span>{data.city}</span>
              </div>
              <div className="item">
                <LanguageIcon />
                <span>{data.username}</span>
              </div>
            </div>
            {rIsLoading 
            ? "Loading" 
            : userId === currentUser.id 
            ? (<button>update</button>) 
            : <button onClick={handleFollow}>{relationshipData.includes(currentUser.id) 
            ? "Following" 
            : "Follow"}
            </button>}
          </div>
          <div className="right">
            <EmailOutlinedIcon />
            <MoreVertIcon />
          </div>
        </div>
      <Posts/>
      </div>
         </>
        )}
    </div>
  );
};

export default Profile;
